private void blockAddIndexes() {

  acquireRead();

  boolean success = false;
  try {

    // Make sure we are still open since we could have
    // waited quite a while for last addIndexes to finish
    ensureOpen(false);
    success = true;
  } finally {
    if (!success)
      releaseRead();
  }
}

