      Lucene50ScoreSkipReader.readImpacts(new ByteArrayDataInput(b), new SimScorer("") {
        int i = 0;

        @Override
        public float score(float freq, long norm) {
          assert freq == freqs[i];
          assert norm == norms[i];
          i++;
          return 0;
        }
      });
    }
  }
}

