/**
 * {@inheritDoc}
 */
@Override
public void readableToIndexed(CharSequence val, BytesRefBuilder result) {
  final String s = val.toString();
  if (s == null)
    return;

  final Integer intValue = stringValueToIntValue(s);
  NumericUtils.intToPrefixCoded(intValue, 0, result);
}

