public synchronized void decRef() throws IOException {
  assert refCount > 0 && (origNorm == null || origNorm.refCount > 0);

  if (--refCount == 0) {
    if (origNorm != null) {
      origNorm.decRef();
      origNorm = null;
    } else {
      closeInput();
    }

    if (bytes != null) {
      assert bytesRef != null;
      bytesRef.decRef();
      bytes = null;
      bytesRef = null;
    } else {
      assert bytesRef == null;
    }
  }
}

