public MMapIndexInput(File file) throws IOException {
  RandomAccessFile raf = new RandomAccessFile(file, "r");
  try {
    this.length = raf.length();
    this.buffer = raf.getChannel().map(MapMode.READ_ONLY, 0, length);
  } finally {
    raf.close();
  }
}

