      @Override
public void finishStage(ResponseBuilder rb) {

  if(!rb.doExpand) {
    return;
  }

  if (rb.stage != ResponseBuilder.STAGE_GET_FIELDS) {
    return;
  }

  Map expanded = (Map)rb.req.getContext().get("expanded");
  if(expanded == null) {
    expanded = new HashMap();
  }

  rb.rsp.add("expanded", expanded);
}

