/** Return the offsetGap from the analyzer assigned to field */
@Override
public int getOffsetGap(Fieldable field) {
  Analyzer analyzer = analyzerMap.get(field.name());
  if (analyzer == null)
    analyzer = defaultAnalyzer;
  return analyzer.getOffsetGap(field);
}

