@Override
public BytesRef getPayload() throws IOException {
  final int len = pos.getPayloadLength();
  if (payload == null) {
    payload = new BytesRef();
    payload.bytes = new byte[len];
  } else {
    if (payload.bytes.length < len) {
      payload.grow(len);
    }
  }

  payload.bytes = pos.getPayload(payload.bytes, 0);
  payload.length = len;
  return payload;
}

