public static String randomSimpleString(Random r, int maxLength) {
  final int end = r.nextInt(maxLength);
  if (end == 0) {
    // allow 0 length
    return "";
  }
  final char[] buffer = new char[end];
  for (int i = 0; i < end; i++) {
    buffer[i] = (char) _TestUtil.nextInt(r, 97, 102);
  }
  return new String(buffer, 0, end);
}

