private void setPageCount() {
    if (totalHits == 0){
        totalPages = 0;
        setCurrentPage(0);
    } else {
        totalPages = totalHits / pageSize;

        //account for remainder if not exaxtly divisable
        if (totalHits % pageSize != 0)
        { totalPages++;}
        setCurrentPage(1); // reset currentPage to make sure not over the limit
    }
}

