private void incRef(List files) throws IOException {
  int size = files.size();
  for(int i=0;i<size;i++) {
    String fileName = (String) files.get(i);
    RefCount rc = getRefCount(fileName);
    if (infoStream != null) {
      message("  IncRef \"" + fileName + "\": pre-incr count is " + rc.count);
    }
    rc.IncRef();
  }
}

