private int getHitCount(Directory dir, Term term) throws IOException {
  IndexSearcher searcher = new IndexSearcher(dir, true);
  int hitCount = searcher.search(new TermQuery(term), null, 1000).totalHits;
  searcher.close();
  return hitCount;
}

