@Override
protected void readInternal(byte[] b, int offset, int len) throws IOException {
  long start = getFilePointer();
  if (start + len > length) {
    throw new EOFException("read past EOF: " + this);
  }
  base.seek(fileOffset + start);
  base.readBytes(b, offset, len, false);
}
