private int readProcessingInstruction() throws IOException {
  // "<?" has already been read

  while (true) {
    int ch = next();
    if (ch=='?' && peek()=='>') {
      next();
      return MATCH;
    } else if (ch==-1) {
      return MISMATCH;
    }
  }
}

