protected SolrClient createNewSolrClient(int port) {
  try {
    // setup the client...
    String baseUrl = buildUrl(port);
    if (baseUrl.endsWith("/")) {
      return getHttpSolrClient(baseUrl + DEFAULT_TEST_CORENAME);
    } else {
      return getHttpSolrClient(baseUrl + "/" + DEFAULT_TEST_CORENAME);
    }
  }
  catch (Exception ex) {
    throw new RuntimeException(ex);
  }
}

