@Override
public Filter makeFilter(SpatialArgs args) {
  //unwrap the CSQ from makeQuery
  ConstantScoreQuery csq = makeQuery(args);
  Filter filter = csq.getFilter();
  if (filter != null)
    return filter;
  else
    return new QueryWrapperFilter(csq.getQuery());
}

