/** Returns the next token in the stream, or null at EOS. */
public final Token next(final Token reusableToken) throws IOException {
  assert reusableToken != null;
  // if we are just starting, read the whole input
  if (!started) {
    started = true;
    char[] chars = new char[1024];
    input.read(chars);
    inStr = new String(chars).trim();  // remove any leading or trailing spaces
    inLen = inStr.length();
    gramSize = minGram;
  }

  // if the remaining input is too short, we can't generate any n-grams
  if (gramSize > inLen) {
    return null;
  }

  // if we have hit the end of our n-gram size range, quit
  if (gramSize > maxGram) {
    return null;
  }

  // grab gramSize chars from front or back
  int start = side == Side.FRONT ? 0 : inLen - gramSize;
  int end = start + gramSize;
  reusableToken.setTermBuffer(inStr, start, gramSize);
  reusableToken.setStartOffset(input.correctOffset(start));
  reusableToken.setEndOffset(input.correctOffset(end));
  gramSize++;
  return reusableToken;
}

