private File createOutput(String name, String text) throws IOException {
    FileWriter fw = null;
    PrintWriter pw = null;

    try {
        File f = new File(workDir, name);
        if (f.exists()) f.delete();

        fw = new FileWriter(f);
        pw = new PrintWriter(fw);
        pw.println(text);
        return f;

    } finally {
        if (pw != null) pw.close();
        if (fw != null) fw.close();
    }
}

