private static IndexReader refreshReader(IndexReader reader) throws IOException {
  IndexReader oldReader = reader;
  reader = reader.reopen();
  if (reader != oldReader) {
    oldReader.close();
  }
  return reader;
}

private static IndexReader refreshReader(IndexReader reader) throws IOException {
  IndexReader oldReader = reader;
  reader = IndexReader.openIfChanged(reader);
  if (reader != null) {
    oldReader.close();
    return reader;
  } else {
    return oldReader;
  }
}

