@Override
protected void handleMergeException(Directory dir, Throwable exc) {
  while (true) {
    if (isOK(exc)) {
      return;
    }
    exc = exc.getCause();
    if (exc == null) {
      super.handleMergeException(dir, exc);
    }
  }
}

