protected int correctPosition( int currentPos ){
  if( pcmList.isEmpty() ) return currentPos;
  for( int i = pcmList.size() - 1; i >= 0; i-- ){
    if( currentPos >= pcmList.get( i ).pos )
      return currentPos + pcmList.get( i ).cumulativeDiff;
  }
  return currentPos;
}

