public void testTotalTermFreq() throws Exception {
  if (Codec.getDefault().getName().equals("Lucene3x")) {
    assertHits(new FunctionQuery(
        new TotalTermFreqValueSource("bogus", "bogus", "text", new BytesRef("test"))),
        new float[] { -1f, -1f });
