private File createTempindexDir(SolrCore core) {
  String snapName = "index."
          + new SimpleDateFormat(SnapShooter.DATE_FMT).format(new Date());
  File snapDir = new File(core.getDataDir(), snapName);
  snapDir.mkdirs();
  return snapDir;
}

