  switch (request.getHttpMethod()) {
    case "GET":
      return Name.AUTOSCALING_READ_PERM;
    case "POST": {
      return StrUtils.splitSmart(request.getResource(), '/', true).size() == 3 ?
          Name.AUTOSCALING_READ_PERM :
          Name.AUTOSCALING_WRITE_PERM;
