/**
 * Set the value for a field.  Arrays will be converted to a collection.
 */
public void setValue(Object v, float b) {
  boost = b;

  if( v instanceof Object[] ) {
    Object[] arr = (Object[])v;
    Collection<Object> c = new ArrayList<Object>( arr.length );
    for( Object o : arr ) {
      c.add( o );
    }
    value = c;
  }
  else {
    value = v;
  }
}

