private RAMDirectory(FSDirectory dir, boolean closeDir, IOContext context) throws IOException {
  this();
  for (String file : dir.listAll()) {
    if (!Files.isDirectory(dir.getDirectory().resolve(file))) {
      copyFrom(dir, file, file, context);
    }
  }
  if (closeDir) {
    dir.close();
  }
}

