private int rollup(int ord) {
  int sum = 0;
  while (ord != TaxonomyReader.INVALID_ORDINAL) {
    int childValue = values[ord] + rollup(children[ord]);
    values[ord] = childValue;
    sum += childValue;
    ord = siblings[ord];
  }
  return sum;
}

