@Override
protected void afterMaybeRefresh() {
  genLock.lock();
  try {
    if (searchingGen != MAX_SEARCHER_GEN) {
      // update searchingGen:
      assert lastRefreshGen >= searchingGen;
      searchingGen = lastRefreshGen;
    }
    // wake up threads if we have a new generation:
    newGeneration.signalAll();
  } finally {
    genLock.unlock();
  }
}

