/**
 * A hook for extending classes to control closing the {@link IndexWriter}
 * returned by {@link #openIndexWriter}.
 */
protected void closeIndexWriter() throws CorruptIndexException, IOException {
  if (indexWriter != null) {
    indexWriter.close();
    indexWriter = null;
  }
}

