@Override
public boolean incrementToken() throws IOException {
  if (input.incrementToken()) {
    if (!keywordAtt.isKeyword()) {
      String baseForm = basicFormAtt.getBaseForm();
      if (baseForm != null) {
        termAtt.setEmpty().append(basicFormAtt.getBaseForm());
      }
    }
    return true;
  } else {
    return false;
  }
}

