public String segString(Directory dir) {
  StringBuffer b = new StringBuffer();
  final int numSegments = segments.size();
  for(int i=0;i<numSegments;i++) {
    if (i > 0) b.append(" ");
    b.append(segments.info(i).segString(dir));
  }
  if (info != null)
    b.append(" into " + info.name);
  if (optimize)
    b.append(" [optimize]");
  return b.toString();
}

