public void testIncreasingSloppinessWithHoles() throws Exception {
  Term t1 = randomTerm();
  Term t2 = randomTerm();
  PhraseQuery q1 = new PhraseQuery();
  q1.add(t1);
  q1.add(t2, 2);
  PhraseQuery q2 = new PhraseQuery();
  q2.add(t1);
  q2.add(t2, 2);
  for (int i = 0; i < 10; i++) {
    q1.setSlop(i);
    q2.setSlop(i+1);
    assertSubsetOf(q1, q2);
  }
}

