public void testIncreasingSloppinessWithHoles() throws Exception {
  Term t1 = randomTerm();
  Term t2 = randomTerm();
  for (int i = 0; i < 10; i++) {
    PhraseQuery.Builder builder = new PhraseQuery.Builder();
    builder.add(t1, 0);
    builder.add(t2, 2);
    builder.setSlop(i);
    PhraseQuery q1 = builder.build();
    builder.setSlop(i + 1);
    PhraseQuery q2 = builder.build();
    assertSubsetOf(q1, q2);
  }
}

