/** Translates the pre-4.0 enums for specifying how a
 *  field should be indexed into the 4.0 {@link FieldType}
 *  approach.
 *
 * @deprecated This is here only to ease transition from
 * the pre-4.0 APIs.
 */
@Deprecated
public static final FieldType translateFieldType(Store store, Index index, TermVector termVector) {
  final FieldType ft = new FieldType();

  ft.setStored(store == Store.YES);

  switch(index) {
  case ANALYZED:
    ft.setIndexed(true);
    ft.setTokenized(true);
    break;
  case ANALYZED_NO_NORMS:
    ft.setIndexed(true);
    ft.setTokenized(true);
    ft.setOmitNorms(true);
    break;
  case NOT_ANALYZED:
    ft.setIndexed(true);
    break;
  case NOT_ANALYZED_NO_NORMS:
    ft.setIndexed(true);
    ft.setOmitNorms(true);
    break;
  case NO:
    break;
  }

  switch(termVector) {
  case NO:
    break;
  case YES:
    ft.setStoreTermVectors(true);
    break;
  case WITH_POSITIONS:
    ft.setStoreTermVectors(true);
    ft.setStoreTermVectorPositions(true);
    break;
  case WITH_OFFSETS:
    ft.setStoreTermVectors(true);
    ft.setStoreTermVectorOffsets(true);
    break;
  case WITH_POSITIONS_OFFSETS:
    ft.setStoreTermVectors(true);
    ft.setStoreTermVectorPositions(true);
    ft.setStoreTermVectorOffsets(true);
    break;
  }
  ft.freeze();
  return ft;
}

