/** returns x == 0 ? 0 : Math.floor(Math.log(x) / Math.log(base)) */
public static int log(long x, int base) {
  assert base > 1;
  int ret = 0;
  while (x >= base) {
    x /= base;
    ret++;
  }
  return ret;
}

