private static String getAnalysisDataDir(File propFile) {
  Properties prop = new Properties();
  try {
    FileInputStream input = new FileInputStream(propFile);
    prop.load(input);
    String dir = prop.getProperty("analysis.data.dir", "");
    input.close();
    return dir;
  } catch (IOException e) {
  }
  return "";
}

