public void dropBuffersAndReset() {
  if (bufferUpto != -1) {
    // Recycle all but the first buffer
    allocator.recycleByteBlocks(buffers, 0, 1+bufferUpto);

    // Re-use the first buffer
    bufferUpto = -1;
    byteUpto = BYTE_BLOCK_SIZE;
    byteOffset = -BYTE_BLOCK_SIZE;
    buffers = new byte[10][];
    buffer = null;
  }
}

