final boolean next() throws IOException {	  // increments to next doc
  if (!tp.next()) {
    tp.close();				  // close stream
    doc = Integer.MAX_VALUE;			  // sentinel value
    return false;
  }
  doc = tp.doc();
  position = 0;
  return true;
}

