@Override
protected synchronized void doClose() throws IOException {
  IOException ioe = null;
  for (final CompositeReader reader : completeReaderSet) {
    try {
      if (closeSubReaders) {
        reader.close();
      } else {
        reader.decRef();
      }
    } catch (IOException e) {
      if (ioe == null) ioe = e;
    }
  }
  // throw the first exception
  if (ioe != null) throw ioe;
}

