private void rmDir(String dirName) {
    File dir = new java.io.File(dirName);
    String[] files = dir.list();            // clear old files
    for (int i = 0; i < files.length; i++) {
        File file = new File(dir, files[i]);
        file.delete();
    }
    dir.delete();
}

