@Override
public void remove(Directory dir) throws IOException {
  synchronized (this) {
    CacheValue val = byDirectoryCache.get(dir);
    if (val == null) {
      throw new IllegalArgumentException("Unknown directory " + dir);
    }
    val.deleteOnClose = true;
  }
}

