/**
 * Shuts down and frees any resources
 */
public void close() {
  if (container != null) {
    for (SolrCore c : container.getCores()) {
      if (c.getOpenCount() > 1)
        throw new RuntimeException("SolrCore.getOpenCount()=="+core.getOpenCount());
    }      
  }

  if (container != null) {
    container.shutdown();
    container = null;
  }
}

