/**
 * Removes a particle denotion ("ge") from a term.
 */
private void removeParticleDenotion( StringBuilder buffer )
{
  if ( buffer.length() > 4 ) {
    for ( int c = 0; c < buffer.length() - 3; c++ ) {
      if ( buffer.substring( c, c + 4 ).equals( "gege" ) ) {
        buffer.delete( c, c + 2 );
        return;
      }
    }
  }
}

