protected void writeLeafBlockDocs(IndexOutput out, int[] docIDs, int start, int count) throws IOException {
  write(out, BLOCK_COUNT);
  writeInt(out, count);
  newline(out);
  for(int i=0;i<count;i++) {
    write(out, BLOCK_DOC_ID);
    writeInt(out, docIDs[start+i]);
    newline(out);
  }
}

