      ddq.add("field", NumericRangeQuery.newLongRange("field", range.min, range.max, range.minInclusive, range.maxInclusive));
      assertEquals(expectedCounts[rangeID], s.search(ddq, 10).totalHits);
    }
  }

  IOUtils.close(w, r, dir);
}

