private static ArrayList<File> rm(ArrayList<File> unremoved, File... locations) {
  for (File location : locations) {
    if (location.exists()) {
      if (location.isDirectory()) {
        rm(unremoved, location.listFiles());
      }

      if (!location.delete()) {
        unremoved.add(location);
      }
    }
  }
  return unremoved;
}

