DocumentsWriterPerThread reset(ThreadState threadState, boolean closed) {
  assert threadState.isHeldByCurrentThread();
  final DocumentsWriterPerThread dwpt = threadState.dwpt;
  if (!closed) {
    threadState.reset();
  } else {
    threadState.deactivate();
  }
  return dwpt;
}

