private boolean equals(char[] text1, int len, char[] text2) {
  if (len != text2.length)
    return false;
  for(int i=0;i<len;i++) {
    if (ignoreCase) {
      if (Character.toLowerCase(text1[i]) != text2[i])
        return false;
    } else {
      if (text1[i] != text2[i])
        return false;
    }
  }
  return true;
}

private boolean equals(CharSequence text1, char[] text2) {
  int len = text1.length();
  if (len != text2.length)
    return false;
  if (ignoreCase) {
    for(int i=0;i<len;i++) {
      if (Character.toLowerCase(text1.charAt(i)) != text2[i])
        return false;
    }
  } else {
    for(int i=0;i<len;i++) {
      if (text1.charAt(i) != text2[i])
        return false;
    }
  }
  return true;
}

