private final void init(Reader input, Version matchVersion) {
  this.scanner = new ClassicTokenizerImpl(input);

  if (matchVersion.onOrAfter(Version.LUCENE_24)) {
    replaceInvalidAcronym = true;
  } else {
    replaceInvalidAcronym = false;
  }
  this.input = input;    
}

