  final NumericDocValues ndv = reader.getNumericDocValues("ndv");
  for (int i = 1; i < reader.maxDoc(); ++i) {
    assertTrue("ndv(" + (i-1) + ")=" + ndv.get(i-1) + ",ndv(" + i + ")=" + ndv.get(i), ndv.get(i-1) <= ndv.get(i));
  }
}

